// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract AirdropStandalone {
    IERC20 public immutable dayaToken;
    address public owner;

    bytes32 public merkleRoot;
    uint256 public claimDeadline;
    mapping(address => bool) public hasClaimed;

    event AirdropStarted(bytes32 indexed merkleRoot, uint256 claimDeadline);
    event TokensClaimed(address indexed claimant, uint256 amount);
    event TokensRecovered(uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _dayaTokenAddress) {
        dayaToken = IERC20(_dayaTokenAddress);
        owner = msg.sender;
    }

    function startAirdrop(bytes32 _merkleRoot, uint256 _claimDeadline) external onlyOwner {
        require(merkleRoot == bytes32(0), "Airdrop already started");
        require(_claimDeadline > block.timestamp, "Deadline must be in the future");

        merkleRoot = _merkleRoot;
        claimDeadline = _claimDeadline;

        emit AirdropStarted(_merkleRoot, _claimDeadline);
    }

    function claim(uint256 _amount, bytes32[] calldata _merkleProof) external {
        require(block.timestamp <= claimDeadline, "Claim period ended");
        require(!hasClaimed[msg.sender], "Already claimed");

        // Verify merkle proof
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender, _amount));
        require(verify(_merkleProof, merkleRoot, leaf), "Invalid proof");

        hasClaimed[msg.sender] = true;
        dayaToken.transfer(msg.sender, _amount);

        emit TokensClaimed(msg.sender, _amount);
    }

    function verify(bytes32[] memory proof, bytes32 root, bytes32 leaf) internal pure returns (bool) {
        bytes32 computedHash = leaf;

        for (uint256 i = 0; i < proof.length; i++) {
            bytes32 proofElement = proof[i];
            if (computedHash <= proofElement) {
                computedHash = keccak256(abi.encodePacked(computedHash, proofElement));
            } else {
                computedHash = keccak256(abi.encodePacked(proofElement, computedHash));
            }
        }

        return computedHash == root;
    }

    function recoverTokens() external onlyOwner {
        require(block.timestamp > claimDeadline, "Claim period not ended");

        uint256 balance = dayaToken.balanceOf(address(this));
        dayaToken.transfer(owner, balance);

        emit TokensRecovered(balance);
    }
}

contracts/DAYATokenStandalone.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title BASED DAYANA Token ($DAYA) - Standalone Auto-Renounce Version
 * @dev A secure ERC-20 token with AUTOMATIC ownership renouncement after 60 days.
 * 
 * This is a standalone version that doesn't depend on OpenZeppelin libraries
 * for easier deployment and compilation.
 *
 * Security Features:
 * - No Transfer Fees: Eliminates fee-based scam patterns
 * - Limited Owner Powers: Reduces centralization risks  
 * - No Blacklist: Removes arbitrary blocking capabilities
 * - AUTOMATIC RENOUNCE: Owner automatically renounced after 60 days
 * - Anti-Reentrancy: Manual implementation of reentrancy protection
 * - Emergency Controls: Limited duration emergency pause (max 72h)
 */
contract DAYATokenStandalone {
    // ERC20 Basic Variables
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    // Ownership
    address private _owner;

    // Reentrancy Protection
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;
    uint256 private _status;

    // Anti-Whale Protection
    uint256 public maxWalletAmount;
    uint256 public maxTxAmount;

    // Trading Control
    bool public tradingEnabled;
    mapping(address => bool) public isExcludedFromLimits;

    // Emergency Controls
    bool public emergencyMode;
    uint256 public emergencyModeEndTime;

    // AUTOMATIC Ownership Renouncement
    uint256 public immutable deploymentTime;
    uint256 public constant AUTO_RENOUNCE_DELAY = 60 days;
    bool public ownershipAutoRenounced;

    // Events
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event TradingEnabled();
    event AutoRenounceExecuted(uint256 timestamp);
    event EmergencyModeActivated(uint256 endTime);
    event EmergencyModeDeactivated();

    // Modifiers
    modifier onlyOwner() {
        require(!isOwnershipAutoRenounced() && msg.sender == _owner, "Not owner or ownership renounced");
        _;
    }

    modifier whenNotPaused() {
        require(!emergencyMode || block.timestamp > emergencyModeEndTime, "Emergency mode active");
        _;
    }

    modifier nonReentrant() {
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");
        _status = _ENTERED;
        _;
        _status = _NOT_ENTERED;
    }

    constructor() {
        _name = "BASED DAYANA";
        _symbol = "DAYA";
        _decimals = 18;
        _totalSupply = 1_000_000_000 * 10**_decimals;

        // Record deployment time for auto-renounce
        deploymentTime = block.timestamp;

        // Set owner
        _owner = msg.sender;

        // Mint total supply to deployer
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);

        // Set reasonable anti-whale limits (2% of total supply)
        uint256 reasonableMax = _totalSupply * 2 / 100;
        maxWalletAmount = reasonableMax;
        maxTxAmount = reasonableMax;

        // Exclude deployer and contract from limits
        isExcludedFromLimits[msg.sender] = true;
        isExcludedFromLimits[address(this)] = true;

        // Initialize reentrancy guard
        _status = _NOT_ENTERED;

        // Trading starts disabled
        tradingEnabled = false;
    }

    // ERC20 Standard Functions
    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 amount) public returns (bool) {
        _transfer(msg.sender, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) public returns (bool) {
        uint256 currentAllowance = _allowances[from][msg.sender];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");

        _transfer(from, to, amount);
        _approve(from, msg.sender, currentAllowance - amount);

        return true;
    }

    // Internal transfer function with all checks
    function _transfer(address from, address to, uint256 amount) internal whenNotPaused {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(_balances[from] >= amount, "ERC20: transfer amount exceeds balance");

        // Check if trading is enabled
        if (!tradingEnabled) {
            require(
                isExcludedFromLimits[from] || isExcludedFromLimits[to], 
                "Trading not yet enabled"
            );
        }

        // Apply anti-whale limits
        if (!isExcludedFromLimits[from] && !isExcludedFromLimits[to]) {
            require(amount <= maxTxAmount, "Transfer amount exceeds maximum");
            require(_balances[to] + amount <= maxWalletAmount, "Wallet would exceed maximum");
        }

        _balances[from] -= amount;
        _balances[to] += amount;

        emit Transfer(from, to, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    // Auto-Renounce Logic
    function isOwnershipAutoRenounced() public view returns (bool) {
        return ownershipAutoRenounced || (block.timestamp >= deploymentTime + AUTO_RENOUNCE_DELAY);
    }

    function getTimeUntilAutoRenounce() external view returns (uint256) {
        if (isOwnershipAutoRenounced()) return 0;
        if (block.timestamp >= deploymentTime + AUTO_RENOUNCE_DELAY) return 0;
        return (deploymentTime + AUTO_RENOUNCE_DELAY) - block.timestamp;
    }

    function executeAutoRenounce() external nonReentrant {
        require(block.timestamp >= deploymentTime + AUTO_RENOUNCE_DELAY, "Auto-renounce time not reached");
        require(!ownershipAutoRenounced, "Already auto-renounced");

        ownershipAutoRenounced = true;
        address previousOwner = _owner;
        _owner = address(0);

        emit OwnershipTransferred(previousOwner, address(0));
        emit AutoRenounceExecuted(block.timestamp);
    }

    function owner() public view returns (address) {
        if (isOwnershipAutoRenounced()) {
            return address(0);
        }
        return _owner;
    }

    // Limited Owner Functions (only work before auto-renounce)
    function enableTrading() external onlyOwner {
        require(!tradingEnabled, "Trading already enabled");
        tradingEnabled = true;
        emit TradingEnabled();
    }

    function increaseMaxWalletAmount(uint256 _newAmount) external onlyOwner {
        require(_newAmount > maxWalletAmount, "Can only increase max wallet amount");
        require(_newAmount <= _totalSupply / 10, "Cannot exceed 10% of total supply");
        maxWalletAmount = _newAmount;
    }

    function increaseMaxTxAmount(uint256 _newAmount) external onlyOwner {
        require(_newAmount > maxTxAmount, "Can only increase max tx amount");
        require(_newAmount <= _totalSupply / 10, "Cannot exceed 10% of total supply");
        maxTxAmount = _newAmount;
    }

    function excludeFromLimits(address _address, bool _excluded) external onlyOwner {
        require(_address != address(0), "Invalid address");
        isExcludedFromLimits[_address] = _excluded;
    }

    function activateEmergencyMode(uint256 _durationHours) external onlyOwner {
        require(_durationHours <= 72, "Emergency mode cannot exceed 72 hours");
        require(!emergencyMode, "Emergency mode already active");

        emergencyMode = true;
        emergencyModeEndTime = block.timestamp + (_durationHours * 1 hours);

        emit EmergencyModeActivated(emergencyModeEndTime);
    }

    function deactivateEmergencyMode() external onlyOwner {
        require(emergencyMode, "Emergency mode not active");

        emergencyMode = false;
        emergencyModeEndTime = 0;

        emit EmergencyModeDeactivated();
    }

    function autoUnpause() external {
        require(emergencyMode && block.timestamp > emergencyModeEndTime, "Emergency mode still active");

        emergencyMode = false;
        emergencyModeEndTime = 0;

        emit EmergencyModeDeactivated();
    }

    // View Functions
    function isFullyDecentralized() external view returns (bool) {
        return isOwnershipAutoRenounced();
    }

    function getAutoRenounceInfo() external view returns (
        uint256 deployTime,
        uint256 renounceTime,
        bool isRenounced,
        uint256 timeRemaining
    ) {
        deployTime = deploymentTime;
        renounceTime = deploymentTime + AUTO_RENOUNCE_DELAY;
        isRenounced = isOwnershipAutoRenounced();

        if (isRenounced) {
            timeRemaining = 0;
        } else if (block.timestamp >= renounceTime) {
            timeRemaining = 0;
        } else {
            timeRemaining = renounceTime - block.timestamp;
        }
    }

    function getEmergencyModeStatus() external view returns (bool active, uint256 timeRemaining) {
        active = emergencyMode && block.timestamp <= emergencyModeEndTime;
        if (active) {
            timeRemaining = emergencyModeEndTime - block.timestamp;
        } else {
            timeRemaining = 0;
        }
    }

    function getSecurityInfo() external pure returns (
        string memory auditStatus,
        string memory securityFeatures,
        string memory riskLevel,
        string memory autoRenounceStatus
    ) {
        auditStatus = "Community verified standalone implementation";
        securityFeatures = "No fees, limited owner powers, automatic renouncement";
        riskLevel = "Ultra Low - Auto-decentralized";
        autoRenounceStatus = "60-day automatic renouncement guaranteed";
    }

    // Burn function (anyone can burn their own tokens)
    function burn(uint256 amount) external nonReentrant {
        require(_balances[msg.sender] >= amount, "Burn amount exceeds balance");

        _balances[msg.sender] -= amount;
        _totalSupply -= amount;

        emit Transfer(msg.sender, address(0), amount);
    }
}

contracts/RewardDistributionStandalone.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract RewardDistributionStandalone {
    IERC20 public immutable dayaToken;
    address public owner;

    struct Pool {
        uint256 totalRewards;
        uint256 rewardPerToken;
        uint256 lastUpdateTime;
        mapping(address => uint256) userRewards;
        mapping(address => uint256) userRewardPerTokenPaid;
    }

    mapping(uint256 => Pool) public pools;
    uint256 public poolCount;

    event RewardAdded(uint256 indexed poolId, uint256 reward);
    event RewardPaid(address indexed user, uint256 indexed poolId, uint256 reward);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _dayaTokenAddress) {
        dayaToken = IERC20(_dayaTokenAddress);
        owner = msg.sender;
    }

    function createPool(uint256 _totalRewards) external onlyOwner {
        Pool storage newPool = pools[poolCount];
        newPool.totalRewards = _totalRewards;
        newPool.lastUpdateTime = block.timestamp;

        poolCount++;

        dayaToken.transferFrom(msg.sender, address(this), _totalRewards);
        emit RewardAdded(poolCount - 1, _totalRewards);
    }

    function claimRewards(uint256 _poolId) external {
        require(_poolId < poolCount, "Invalid pool");

        Pool storage pool = pools[_poolId];
        uint256 reward = pool.userRewards[msg.sender];

        if (reward > 0) {
            pool.userRewards[msg.sender] = 0;
            dayaToken.transfer(msg.sender, reward);
            emit RewardPaid(msg.sender, _poolId, reward);
        }
    }

    function addUserToPool(uint256 _poolId, address _user, uint256 _rewardAmount) external onlyOwner {
        require(_poolId < poolCount, "Invalid pool");
        pools[_poolId].userRewards[_user] += _rewardAmount;
    }
}

contracts/StakingStandalone.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract StakingStandalone {
    IERC20 public immutable dayaToken;
    address public owner;

    struct Stake {
        uint256 amount;
        uint256 since;
        uint256 stakingPeriod;
        uint256 rewards;
        bool compounded;
    }

    mapping(address => Stake) public stakes;

    struct StakingTier {
        uint256 period; // in days
        uint256 apyBps; // Annual Percentage Yield in basis points
    }

    StakingTier[] public stakingTiers;

    uint256 public earlyUnstakePenaltyBps = 2500; // 25% penalty
    address public penaltyWallet;

    event Staked(address indexed user, uint256 amount, uint256 period);
    event Unstaked(address indexed user, uint256 amount, uint256 rewards);
    event RewardsCompounded(address indexed user, uint256 newAmount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _dayaTokenAddress) {
        dayaToken = IERC20(_dayaTokenAddress);
        owner = msg.sender;
        penaltyWallet = owner;

        // Initialize staking tiers
        stakingTiers.push(StakingTier(30, 1000)); // 10% APY
        stakingTiers.push(StakingTier(90, 1500)); // 15% APY
        stakingTiers.push(StakingTier(180, 2000)); // 20% APY
        stakingTiers.push(StakingTier(365, 3000)); // 30% APY
    }

    function stake(uint256 _amount, uint256 _tierIndex) external {
        require(_amount > 0, "Amount must be greater than 0");
        require(_tierIndex < stakingTiers.length, "Invalid tier");
        require(stakes[msg.sender].amount == 0, "Already staking");

        dayaToken.transferFrom(msg.sender, address(this), _amount);

        stakes[msg.sender] = Stake({
            amount: _amount,
            since: block.timestamp,
            stakingPeriod: stakingTiers[_tierIndex].period,
            rewards: 0,
            compounded: false
        });

        emit Staked(msg.sender, _amount, stakingTiers[_tierIndex].period);
    }

    function unstake() external {
        Stake storage userStake = stakes[msg.sender];
        require(userStake.amount > 0, "No stake found");

        uint256 stakingDuration = block.timestamp - userStake.since;
        uint256 rewards = calculateRewards(msg.sender);

        uint256 totalAmount = userStake.amount + rewards;

        // Apply penalty if unstaking early
        if (stakingDuration < userStake.stakingPeriod * 1 days) {
            uint256 penalty = (totalAmount * earlyUnstakePenaltyBps) / 10000;
            totalAmount -= penalty;
            dayaToken.transfer(penaltyWallet, penalty);
        }

        delete stakes[msg.sender];
        dayaToken.transfer(msg.sender, totalAmount);

        emit Unstaked(msg.sender, userStake.amount, rewards);
    }

    function calculateRewards(address _staker) public view returns (uint256) {
        Stake memory userStake = stakes[_staker];
        if (userStake.amount == 0) return 0;

        uint256 stakingDuration = block.timestamp - userStake.since;
        uint256 annualReward = (userStake.amount * getAPYForPeriod(userStake.stakingPeriod)) / 10000;
        uint256 rewards = (annualReward * stakingDuration) / (365 days);

        return rewards;
    }

    function getAPYForPeriod(uint256 _period) internal view returns (uint256) {
        for (uint256 i = 0; i < stakingTiers.length; i++) {
            if (stakingTiers[i].period == _period) {
                return stakingTiers[i].apyBps;
            }
        }
        return 1000; // Default 10% APY
    }
}

contracts/VestingStandalone.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

contract VestingStandalone {
    IERC20 public immutable dayaToken;
    address public owner;

    struct VestingSchedule {
        uint256 totalAmount;
        uint256 start;
        uint256 cliff;
        uint256 duration;
        uint256 released;
        bool revocable;
        bool revoked;
    }

    mapping(address => VestingSchedule) public vestingSchedules;

    event VestingScheduleCreated(address indexed beneficiary, uint256 totalAmount, uint256 start, uint256 duration);
    event TokensReleased(address indexed beneficiary, uint256 amount);
    event VestingRevoked(address indexed beneficiary);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _dayaTokenAddress) {
        dayaToken = IERC20(_dayaTokenAddress);
        owner = msg.sender;
    }

    function createVestingSchedule(
        address _beneficiary,
        uint256 _totalAmount,
        uint256 _cliff,
        uint256 _duration,
        bool _revocable
    ) external onlyOwner {
        require(_beneficiary != address(0), "Invalid beneficiary");
        require(_totalAmount > 0, "Amount must be greater than 0");
        require(vestingSchedules[_beneficiary].totalAmount == 0, "Schedule already exists");

        vestingSchedules[_beneficiary] = VestingSchedule({
            totalAmount: _totalAmount,
            start: block.timestamp,
            cliff: _cliff,
            duration: _duration,
            released: 0,
            revocable: _revocable,
            revoked: false
        });

        dayaToken.transferFrom(msg.sender, address(this), _totalAmount);

        emit VestingScheduleCreated(_beneficiary, _totalAmount, block.timestamp, _duration);
    }

    function release() external {
        VestingSchedule storage schedule = vestingSchedules[msg.sender];
        require(schedule.totalAmount > 0, "No vesting schedule");
        require(!schedule.revoked, "Vesting revoked");

        uint256 releasableAmount = getReleasableAmount(msg.sender);
        require(releasableAmount > 0, "No tokens to release");

        schedule.released += releasableAmount;
        dayaToken.transfer(msg.sender, releasableAmount);

        emit TokensReleased(msg.sender, releasableAmount);
    }

    function getReleasableAmount(address _beneficiary) public view returns (uint256) {
        VestingSchedule memory schedule = vestingSchedules[_beneficiary];
        if (schedule.revoked || block.timestamp < schedule.start + schedule.cliff) {
            return 0;
        }

        uint256 elapsedTime = block.timestamp - schedule.start;
        uint256 vestedAmount;

        if (elapsedTime >= schedule.duration) {
            vestedAmount = schedule.totalAmount;
        } else {
            vestedAmount = (schedule.totalAmount * elapsedTime) / schedule.duration;
        }

        return vestedAmount - schedule.released;
    }

    function revokeVesting(address _beneficiary) external onlyOwner {
        VestingSchedule storage schedule = vestingSchedules[_beneficiary];
        require(schedule.revocable, "Not revocable");
        require(!schedule.revoked, "Already revoked");

        uint256 releasableAmount = getReleasableAmount(_beneficiary);
        if (releasableAmount > 0) {
            schedule.released += releasableAmount;
            dayaToken.transfer(_beneficiary, releasableAmount);
        }

        schedule.revoked = true;
        uint256 remainingAmount = schedule.totalAmount - schedule.released;
        if (remainingAmount > 0) {
            dayaToken.transfer(owner, remainingAmount);
        }

        emit VestingRevoked(_beneficiary);
    }
}

